import { ChevronRight } from "lucide-react";
import { Knot } from "./Knot";
import { Card } from "@/components/ui/card";

interface Chapter {
  id: string;
  title: string;
  emoji?: string;
  order: number;
  isPublic: boolean;
  pageCount?: number;
}

