  chapters: Chapter[];
  activeChapterId?: string;
  onChapterClick?: (chapterId: string) => void;
}

export function ChapterNav({ chapters, activeChapterId, onChapterClick }: ChapterNavProps) {
  return (
    <div className="space-y-2">
