import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { BarChart, Bar, LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Cell } from "recharts";
import { Book, BookOpen, FileText, Users, TrendingUp, Eye } from "lucide-react";

interface AnalyticsData {
  overview: {
    totalChapters: number;
    totalSections: number;
    totalPages: number;
    totalReaders: number;
  };
  chapterCompletion: Array<{
    chapterId: string;
    chapterTitle: string;
    totalSections: number;
    completedCount: number;
    completionRate: number;
  }>;
  sectionViews: Array<{
    sectionId: string;
    sectionTitle: string;
    chapterTitle: string;
    viewCount: number;
    avgDuration: number;
  }>;
  activityTimeline: Array<{
    date: string;
    viewCount: number;
    uniqueReaders: number;
  }>;
}

export function AnalyticsDashboard() {
  const { data: analytics, isLoading } = useQuery<AnalyticsData>({
    queryKey: ["/api/analytics/dashboard"],
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <p className="font-noto text-muted-foreground">Loading analytics...</p>
      </div>
    );
  }

  if (!analytics) {
    return (
      <div className="flex items-center justify-center py-12">
        <p className="font-noto text-muted-foreground">No analytics data available</p>
      </div>
    );
  }

  const CHART_COLORS = ['#E63946', '#F4A6B3', '#C7B8EA', '#A8DADC', '#457B9D'];

  return (
    <div className="space-y-6">
      <div>
        <h2 className="font-myeongjo text-2xl font-bold text-kdrama-ink dark:text-foreground">
          Analytics Dashboard
        </h2>
        <p className="font-noto text-sm text-muted-foreground mt-1">
          View engagement metrics across all chapters and sections
        </p>
      </div>

      {/* Overview Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between gap-1 space-y-0 pb-2">
            <CardTitle className="font-noto text-sm font-medium">Total Chapters</CardTitle>
            <Book className="w-4 h-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="font-myeongjo text-2xl font-bold text-kdrama-thread">
              {analytics.overview.totalChapters}
            </div>
            <p className="font-noto text-xs text-muted-foreground">
              Published chapters
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between gap-1 space-y-0 pb-2">
            <CardTitle className="font-noto text-sm font-medium">Total Sections</CardTitle>
            <BookOpen className="w-4 h-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="font-myeongjo text-2xl font-bold text-kdrama-thread">
              {analytics.overview.totalSections}
            </div>
            <p className="font-noto text-xs text-muted-foreground">
              Content sections
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between gap-1 space-y-0 pb-2">
            <CardTitle className="font-noto text-sm font-medium">Total Pages</CardTitle>
            <FileText className="w-4 h-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="font-myeongjo text-2xl font-bold text-kdrama-thread">
              {analytics.overview.totalPages}
            </div>
            <p className="font-noto text-xs text-muted-foreground">
              Content pages
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between gap-1 space-y-0 pb-2">
            <CardTitle className="font-noto text-sm font-medium">Total Readers</CardTitle>
            <Users className="w-4 h-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="font-myeongjo text-2xl font-bold text-kdrama-thread">
              {analytics.overview.totalReaders}
            </div>
            <p className="font-noto text-xs text-muted-foreground">
              Active users
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Chapter Completion Chart */}
      <Card>
        <CardHeader>
          <CardTitle className="font-myeongjo flex items-center gap-2">
            <TrendingUp className="w-5 h-5 text-kdrama-thread" />
            Chapter Completion Rates
          </CardTitle>
          <CardDescription className="font-noto">
            Percentage of readers who completed each chapter
          </CardDescription>
        </CardHeader>
        <CardContent>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={analytics.chapterCompletion}>
              <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
              <XAxis
                dataKey="chapterTitle"
                stroke="hsl(var(--muted-foreground))"
                fontSize={12}
                tickLine={false}
                axisLine={false}
              />
              <YAxis
                stroke="hsl(var(--muted-foreground))"
                fontSize={12}
                tickLine={false}
                axisLine={false}
                tickFormatter={(value) => `${value}%`}
              />
              <Tooltip
                contentStyle={{
                  backgroundColor: "hsl(var(--card))",
                  border: "1px solid hsl(var(--border))",
                  borderRadius: "6px",
                }}
                labelStyle={{ fontFamily: "Nanum Myeongjo" }}
                formatter={(value: number) => [`${value.toFixed(1)}%`, "Completion Rate"]}
              />
              <Bar dataKey="completionRate" fill="#E63946" radius={[4, 4, 0, 0]} />
            </BarChart>
          </ResponsiveContainer>
        </CardContent>
      </Card>

      {/* Section Views Chart */}
      <Card>
        <CardHeader>
          <CardTitle className="font-myeongjo flex items-center gap-2">
            <Eye className="w-5 h-5 text-kdrama-thread" />
            Section Engagement by Views
          </CardTitle>
          <CardDescription className="font-noto">
            Most-read sections across all chapters
          </CardDescription>
        </CardHeader>
        <CardContent>
          {analytics.sectionViews.length > 0 ? (
            <ResponsiveContainer width="100%" height={400}>
              <BarChart
                data={analytics.sectionViews.slice(0, 10)}
                layout="vertical"
                margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
              >
                <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
                <XAxis
                  type="number"
                  stroke="hsl(var(--muted-foreground))"
                  fontSize={12}
                  tickLine={false}
                  axisLine={false}
                />
                <YAxis
                  type="category"
                  dataKey="sectionTitle"
                  stroke="hsl(var(--muted-foreground))"
                  fontSize={12}
                  tickLine={false}
                  axisLine={false}
                  width={150}
                />
                <Tooltip
                  contentStyle={{
                    backgroundColor: "hsl(var(--card))",
                    border: "1px solid hsl(var(--border))",
                    borderRadius: "6px",
                  }}
                  labelStyle={{ fontFamily: "Nanum Myeongjo" }}
                  formatter={(value: number, name: string) => {
                    if (name === "viewCount") return [value, "Views"];
                    return [value, name];
                  }}
                  labelFormatter={(label: string, payload: any[]) => {
                    const item = payload[0]?.payload;
                    if (item) {
                      return `${item.sectionTitle} (${item.chapterTitle})`;
                    }
                    return label;
                  }}
                />
                <Bar dataKey="viewCount" fill="#C7B8EA" radius={[0, 4, 4, 0]}>
                  {analytics.sectionViews.slice(0, 10).map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={CHART_COLORS[index % CHART_COLORS.length]} />
                  ))}
                </Bar>
              </BarChart>
            </ResponsiveContainer>
          ) : (
            <p className="font-noto text-sm text-muted-foreground text-center py-8">
              No section views yet
            </p>
          )}
        </CardContent>
      </Card>

      {/* Activity Timeline Chart */}
      <Card>
        <CardHeader>
          <CardTitle className="font-myeongjo flex items-center gap-2">
            <TrendingUp className="w-5 h-5 text-kdrama-thread" />
            Reading Activity Over Time
          </CardTitle>
          <CardDescription className="font-noto">
            Daily reading patterns for all content
          </CardDescription>
        </CardHeader>
        <CardContent>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={analytics.activityTimeline}>
              <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
              <XAxis
                dataKey="date"
                stroke="hsl(var(--muted-foreground))"
                fontSize={12}
                tickLine={false}
                axisLine={false}
              />
              <YAxis
                stroke="hsl(var(--muted-foreground))"
                fontSize={12}
                tickLine={false}
                axisLine={false}
              />
              <Tooltip
                contentStyle={{
                  backgroundColor: "hsl(var(--card))",
                  border: "1px solid hsl(var(--border))",
                  borderRadius: "6px",
                }}
                labelStyle={{ fontFamily: "Nanum Myeongjo" }}
              />
              <Legend wrapperStyle={{ fontFamily: "Noto Sans KR" }} />
              <Line
                type="monotone"
                dataKey="viewCount"
                stroke="#E63946"
                strokeWidth={2}
                name="Page Views"
                dot={{ fill: "#E63946" }}
              />
              <Line
                type="monotone"
                dataKey="uniqueReaders"
                stroke="#C7B8EA"
                strokeWidth={2}
                name="Unique Readers"
                dot={{ fill: "#C7B8EA" }}
              />
            </LineChart>
          </ResponsiveContainer>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/admin/ContentManag.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Plus, Pencil, Trash2, GripVertical, FileText, ChevronDown, ChevronRight, ArrowUp, ArrowDown } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { Section, Page, Chapter } from "@shared/schema";
import { TiptapEditor } from "@/components/TiptapEditor";

export function ContentManagement() {
  const { toast } = useToast();
  const [expandedSections, setExpandedSections] = useState<Set<string>>(new Set());
  const [editingSection, setEditingSection] = useState<Section | null>(null);
  const [editingPage, setEditingPage] = useState<{ page: Page; sectionId: string } | null>(null);
  const [creatingSection, setCreatingSection] = useState(false);
  const [creatingPageFor, setCreatingPageFor] = useState<string | null>(null);

  const { data: chapters = [] } = useQuery<Chapter[]>({
    queryKey: ["/api/chapters"],
  });

  const { data: sections = [] } = useQuery<Section[]>({
    queryKey: ["/api/sections"],
  });

  const { data: allPages = [] } = useQuery<Page[]>({
    queryKey: ["/api/pages"],
  });

  const toggleSection = (sectionId: string) => {
    const newExpanded = new Set(expandedSections);
    if (newExpanded.has(sectionId)) {
      newExpanded.delete(sectionId);
    } else {
      newExpanded.add(sectionId);
    }
    setExpandedSections(newExpanded);
  };

  const getSectionPages = (sectionId: string) => {
    return allPages.filter(p => p.sectionId === sectionId).sort((a, b) => a.pageNumber - b.pageNumber);
  };

  const deleteSectionMutation = useMutation({
    mutationFn: (id: string) => apiRequest("DELETE", `/api/sections/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/sections"] });
      queryClient.invalidateQueries({ queryKey: ["/api/pages"] });
      toast({ title: "Section deleted successfully" });
    },
  });

  const deletePageMutation = useMutation({
    mutationFn: (id: string) => apiRequest("DELETE", `/api/pages/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/pages"] });
      toast({ title: "Page deleted successfully" });
    },
  });

  const reorderSectionsMutation = useMutation({
    mutationFn: (sectionOrders: { id: string; order: number }[]) =>
      apiRequest("PATCH", "/api/sections/reorder", { sectionOrders }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/sections"] });
      toast({ title: "Section order updated" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Failed to reorder sections", 
        description: error?.message || "Please try again",
        variant: "destructive" 
      });
    },
  });

  const moveSectionUp = (section: Section) => {
    const chapterSections = sections
      .filter(s => s.chapterId === section.chapterId)
      .sort((a, b) => a.order - b.order);

    const currentIndex = chapterSections.findIndex(s => s.id === section.id);
    if (currentIndex <= 0) return;

    const sectionOrders = chapterSections.map((s, idx) => {
      if (idx === currentIndex - 1) {
        return { id: s.id, order: section.order };
      } else if (idx === currentIndex) {
        return { id: s.id, order: chapterSections[currentIndex - 1].order };
      }
      return { id: s.id, order: s.order };
    });

    reorderSectionsMutation.mutate(sectionOrders);
  };

  const moveSectionDown = (section: Section) => {
    const chapterSections = sections
      .filter(s => s.chapterId === section.chapterId)
      .sort((a, b) => a.order - b.order);

    const currentIndex = chapterSections.findIndex(s => s.id === section.id);
    if (currentIndex === -1 || currentIndex >= chapterSections.length - 1) return;

    const sectionOrders = chapterSections.map((s, idx) => {
      if (idx === currentIndex) {
        return { id: s.id, order: chapterSections[currentIndex + 1].order };
      } else if (idx === currentIndex + 1) {
        return { id: s.id, order: section.order };
      }
      return { id: s.id, order: s.order };
    });

    reorderSectionsMutation.mutate(sectionOrders);
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="font-myeongjo text-2xl font-bold text-kdrama-ink dark:text-foreground">
            Content Management
          </h2>
          <p className="font-noto text-sm text-muted-foreground mt-1">
            Manage sections and their pages in one place
          </p>
        </div>
        <Button onClick={() => setCreatingSection(true)} data-testid="button-create-section">
          <Plus className="w-4 h-4 mr-2" />
          Create Section
        </Button>
      </div>

      {/* Sections List with Nested Pages */}
      <div className="space-y-4">
        {sections.length === 0 ? (
          <Card>
            <CardContent className="flex items-center justify-center py-12">
              <p className="font-noto text-muted-foreground">No sections yet. Create your first section to get started.</p>
            </CardContent>
          </Card>
        ) : (
          sections.map((section) => {
            const chapter = chapters.find(c => c.id === section.chapterId);
            const sectionPages = getSectionPages(section.id);
            const isExpanded = expandedSections.has(section.id);

            return (
              <Card key={section.id} data-testid={`card-section-${section.id}`}>
                <CardHeader>
                  <div className="flex items-start justify-between gap-4">
                    <div className="flex items-start gap-3 flex-1 min-w-0">
                      <Button
                        variant="ghost"
                        size="icon"
                        className="shrink-0 mt-1"
                        onClick={() => toggleSection(section.id)}
                        data-testid={`button-toggle-${section.id}`}
                      >
                        {isExpanded ? <ChevronDown className="w-4 h-4" /> : <ChevronRight className="w-4 h-4" />}
                      </Button>
                      <div className="flex-1 min-w-0">
                        <CardTitle className="font-myeongjo text-lg">{section.title}</CardTitle>
                        <CardDescription className="font-noto mt-1">
                          {chapter?.title}  {sectionPages.length} page{sectionPages.length !== 1 ? 's' : ''}
                        </CardDescription>
                        {((section.mood && section.mood.length > 0) || (section.tags && section.tags.length > 0)) && (
                          <div className="flex flex-wrap gap-2 mt-2">
                            {section.mood?.map((mood, idx) => (
                              <Badge key={`mood-${idx}`} variant="secondary" className="font-noto text-xs">
                                {mood}
                              </Badge>
                            ))}
                            {section.tags?.map((tag) => (
                              <Badge key={tag} variant="outline" className="font-noto text-xs">
                                #{tag}
                              </Badge>
