import { useState, useRef } from "react";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { X, Upload, Plus } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface ImageGalleryDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onInsert: (html: string) => void;
}

export function ImageGalleryDialog({ open, onOpenChange, onInsert }: ImageGalleryDialogProps) {
  const [imageUrls, setImageUrls] = useState<string[]>([]);
  const [currentUrl, setCurrentUrl] = useState("");
  const [uploadMode, setUploadMode] = useState<"url" | "file">("file");
  const [selectedFiles, setSelectedFiles] = useState<File[]>([]);
  const [previewUrls, setPreviewUrls] = useState<string[]>([]);
  const [uploading, setUploading] = useState(false);
  const [isDragging, setIsDragging] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const handleFilesSelect = (files: FileList | File[]) => {
    const fileArray = Array.from(files);
    const validFiles: File[] = [];
    const newPreviews: string[] = [];

    fileArray.forEach((file) => {
      // Validate file type
      const validTypes = ['image/jpeg', 'image/png', 'image/gif', 'image/webp'];
      if (!validTypes.includes(file.type)) {
        toast({
          title: "Invalid file type",
          description: `${file.name} is not a valid image. Please select JPG, PNG, GIF, or WebP images.`,
          variant: "destructive",
        });
        return;
      }

      // Validate file size (10MB)
      if (file.size > 10 * 1024 * 1024) {
        toast({
          title: "File too large",
          description: `${file.name} is larger than 10MB.`,
          variant: "destructive",
        });
        return;
      }

      validFiles.push(file);

      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => {
        newPreviews.push(e.target?.result as string);
        if (newPreviews.length === validFiles.length) {
          setPreviewUrls((prev) => [...prev, ...newPreviews]);
        }
      };
      reader.readAsDataURL(file);
    });

    setSelectedFiles((prev) => [...prev, ...validFiles]);
  };

  const handleFileInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      handleFilesSelect(e.target.files);
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      handleFilesSelect(e.dataTransfer.files);
    }
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const uploadFiles = async (files: File[]): Promise<string[]> => {
    const uploadPromises = files.map(async (file) => {
      const formData = new FormData();
      formData.append('image', file);

      const response = await fetch('/api/upload/image', {
        method: 'POST',
        body: formData,
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error(`Failed to upload ${file.name}`);
      }

      const data = await response.json();
      return data.url;
    });

    return Promise.all(uploadPromises);
  };

  const handleAddUrl = () => {
    if (currentUrl.trim()) {
      setImageUrls((prev) => [...prev, currentUrl.trim()]);
      setCurrentUrl("");
    }
  };

  const handleRemoveImage = (index: number) => {
    if (uploadMode === "file") {
      setSelectedFiles((prev) => prev.filter((_, i) => i !== index));
      setPreviewUrls((prev) => prev.filter((_, i) => i !== index));
    } else {
      setImageUrls((prev) => prev.filter((_, i) => i !== index));
    }
  };

  const handleInsert = async () => {
    let finalUrls: string[] = [];

    if (uploadMode === "file") {
      if (selectedFiles.length === 0) {
        toast({
          title: "No images selected",
          description: "Please select at least one image.",
          variant: "destructive",
        });
        return;
      }

      setUploading(true);
      try {
        finalUrls = await uploadFiles(selectedFiles);
      } catch (error) {
        toast({
          title: "Upload failed",
          description: error instanceof Error ? error.message : "Failed to upload images.",
          variant: "destructive",
        });
        setUploading(false);
        return;
      }
      setUploading(false);
    } else {
      if (imageUrls.length === 0) {
        toast({
          title: "No images added",
          description: "Please add at least one image URL.",
          variant: "destructive",
        });
        return;
      }
      finalUrls = imageUrls;
    }

    // Create gallery embed code
    const embedCode = `[gallery:${finalUrls.join(',')}]`;
    onInsert(embedCode);

    // Reset form
    setImageUrls([]);
    setCurrentUrl("");
    setSelectedFiles([]);
    setPreviewUrls([]);
    setUploadMode("file");
    onOpenChange(false);
  };

  const totalImages = uploadMode === "file" ? selectedFiles.length : imageUrls.length;
  const previews = uploadMode === "file" ? previewUrls : imageUrls;

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="font-myeongjo">Insert Image Gallery</DialogTitle>
          <DialogDescription className="font-noto">
            Add multiple images that will display in a beautiful grid
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4 py-4">
          {/* Mode Toggle */}
          <div className="flex gap-2">
            <Button
              type="button"
              variant={uploadMode === "file" ? "default" : "outline"}
              size="sm"
              onClick={() => setUploadMode("file")}
              data-testid="button-mode-file"
            >
              <Upload className="w-4 h-4 mr-1" />
              Upload Files
            </Button>
            <Button
              type="button"
              variant={uploadMode === "url" ? "default" : "outline"}
              size="sm"
              onClick={() => setUploadMode("url")}
              data-testid="button-mode-url"
            >
              From URLs
            </Button>
          </div>

          {uploadMode === "file" ? (
            <div>
              <Label className="font-noto">Upload Images</Label>
              <div
                className={`mt-1.5 border-2 border-dashed rounded-lg p-8 text-center cursor-pointer transition-colors ${
                  isDragging ? 'border-kdrama-primary bg-kdrama-primary/10' : 'border-muted-foreground/25 hover:border-kdrama-primary/50'
                }`}
                onDrop={handleDrop}
                onDragOver={handleDragOver}
                onDragLeave={handleDragLeave}
                onClick={() => fileInputRef.current?.click()}
                data-testid="dropzone-gallery"
              >
                <input
                  ref={fileInputRef}
                  type="file"
                  accept="image/*"
                  multiple
                  onChange={handleFileInputChange}
                  className="hidden"
                  data-testid="input-file-multiple"
                />
                <Upload className="w-12 h-12 mx-auto mb-3 text-muted-foreground" />
                <p className="text-sm font-noto mb-1">
                  {selectedFiles.length === 0 ? "Drop images here or click to browse" : `${selectedFiles.length} images selected`}
                </p>
                <p className="text-xs text-muted-foreground">
                  Select multiple images (JPG, PNG, GIF, WebP - max 10MB each)
                </p>
              </div>
            </div>
          ) : (
            <div>
              <Label className="font-noto">Add Image URLs</Label>
              <div className="flex gap-2 mt-1.5">
                <Input
                  type="url"
                  value={currentUrl}
                  onChange={(e) => setCurrentUrl(e.target.value)}
                  placeholder="https://example.com/image.jpg"
                  onKeyDown={(e) => {
                    if (e.key === "Enter") {
                      e.preventDefault();
                      handleAddUrl();
                    }
                  }}
                  data-testid="input-gallery-url"
                />
                <Button
                  type="button"
                  variant="outline"
                  size="icon"
                  onClick={handleAddUrl}
                  disabled={!currentUrl.trim()}
                  data-testid="button-add-url"
                >
                  <Plus className="w-4 h-4" />
                </Button>
              </div>
            </div>
          )}

          {/* Preview Grid */}
          {totalImages > 0 && (
            <div>
              <Label className="font-noto">Preview ({totalImages} images)</Label>
              <div className="grid grid-cols-3 gap-3 mt-2">
                {previews.map((preview, index) => (
                  <div
                    key={index}
                    className="relative aspect-square rounded-lg overflow-hidden group"
                    data-testid={`preview-${index}`}
                  >
                    <img
                      src={preview}
                      alt={`Preview ${index + 1}`}
                      className="w-full h-full object-cover"
                    />
                    <button
                      type="button"
                      onClick={() => handleRemoveImage(index)}
                      className="absolute top-1 right-1 bg-destructive text-destructive-foreground rounded-full p-1 opacity-0 group-hover:opacity-100 transition-opacity"
                      data-testid={`button-remove-${index}`}
                    >
                      <X className="w-3 h-3" />
                    </button>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        <DialogFooter>
          <Button
            type="button"
            variant="outline"
            onClick={() => onOpenChange(false)}
            data-testid="button-cancel"
          >
            Cancel
          </Button>
          <Button
            onClick={handleInsert}
            disabled={totalImages === 0 || uploading}
            data-testid="button-insert-gallery"
          >
            {uploading ? "Uploading..." : `Insert Gallery (${totalImages})`}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

