import { useState } from "react";
import { X, ChevronLeft, ChevronRight, ZoomIn, ZoomOut } from "lucide-react";
import { Button } from "@/components/ui/button";

interface ImageGalleryProps {
  images: string[];
  className?: string;
}

export function ImageGallery({ images, className = "" }: ImageGalleryProps) {
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [zoom, setZoom] = useState(1);

  const openLightbox = (index: number) => {
    setCurrentImageIndex(index);
    setLightboxOpen(true);
    setZoom(1);
  };

  const closeLightbox = () => {
    setLightboxOpen(false);
    setZoom(1);
  };

  const goToPrevious = () => {
    setCurrentImageIndex((prev) => (prev === 0 ? images.length - 1 : prev - 1));
    setZoom(1);
  };

  const goToNext = () => {
    setCurrentImageIndex((prev) => (prev === images.length - 1 ? 0 : prev + 1));
    setZoom(1);
  };

  const handleZoomIn = () => {
    setZoom((prev) => Math.min(prev + 0.25, 3));
  };

  const handleZoomOut = () => {
    setZoom((prev) => Math.max(prev - 0.25, 0.5));
  };

  const handleKeyDown = (e: KeyboardEvent) => {
    if (!lightboxOpen) return;

    if (e.key === "Escape") closeLightbox();
    if (e.key === "ArrowLeft") goToPrevious();
    if (e.key === "ArrowRight") goToNext();
  };

  // Add keyboard listeners
  useState(() => {
    window.addEventListener("keydown", handleKeyDown as any);
    return () => window.removeEventListener("keydown", handleKeyDown as any);
  });

  if (images.length === 0) return null;

  // Single image layout
  if (images.length === 1) {
    return (
      <>
        <div className={`my-6 ${className}`}>
          <img
            src={images[0]}
            alt="Content image"
            className="w-full rounded-lg shadow-md hover-elevate active-elevate-2 cursor-pointer transition-all"
            onClick={() => openLightbox(0)}
            data-testid="img-single"
          />
        </div>
        {lightboxOpen && (
          <Lightbox
            image={images[0]}
            onClose={closeLightbox}
            zoom={zoom}
            onZoomIn={handleZoomIn}
            onZoomOut={handleZoomOut}
          />
        )}
      </>
    );
  }

  // Gallery grid layout for 2+ images
  const gridClass =
    images.length === 2
      ? "grid-cols-2"
      : images.length === 3
      ? "grid-cols-3"
      : images.length === 4
      ? "grid-cols-2"
      : "grid-cols-3";

  return (
    <>
      <div className={`my-6 ${className}`}>
        <div className={`grid ${gridClass} gap-3`}>
          {images.map((image, index) => (
            <div
              key={index}
              className="relative aspect-square overflow-hidden rounded-lg shadow-md hover-elevate active-elevate-2 cursor-pointer transition-all"
              onClick={() => openLightbox(index)}
              data-testid={`img-gallery-${index}`}
            >
              <img
                src={image}
                alt={`Gallery image ${index + 1}`}
                className="w-full h-full object-cover"
              />
            </div>
          ))}
        </div>
        <p className="text-center text-sm text-muted-foreground mt-3 font-noto">
          {images.length} images  Click to view
        </p>
      </div>

      {lightboxOpen && (
        <Lightbox
          image={images[currentImageIndex]}
          currentIndex={currentImageIndex}
          total={images.length}
          onClose={closeLightbox}
          onPrevious={goToPrevious}
          onNext={goToNext}
          zoom={zoom}
          onZoomIn={handleZoomIn}
          onZoomOut={handleZoomOut}
        />
      )}
    </>
  );
}

interface LightboxProps {
  image: string;
  currentIndex?: number;
  total?: number;
  onClose: () => void;
  onPrevious?: () => void;
  onNext?: () => void;
  zoom: number;
  onZoomIn: () => void;
  onZoomOut: () => void;
}

function Lightbox({
  image,
  currentIndex,
  total,
  onClose,
  onPrevious,
  onNext,
  zoom,
  onZoomIn,
  onZoomOut,
}: LightboxProps) {
  return (
    <div
      className="fixed inset-0 z-50 bg-black/95 flex items-center justify-center"
      onClick={onClose}
      data-testid="lightbox-overlay"
    >
      {/* Close button */}
      <Button
        size="icon"
        variant="ghost"
        className="absolute top-4 right-4 text-white hover:bg-white/20 z-10"
        onClick={(e) => {
          e.stopPropagation();
          onClose();
        }}
        data-testid="button-close-lightbox"
      >
        <X className="w-6 h-6" />
      </Button>

      {/* Image counter */}
      {total && total > 1 && (
        <div className="absolute top-4 left-1/2 -translate-x-1/2 text-white font-myeongjo text-lg z-10">
          {currentIndex! + 1} / {total}
        </div>
      )}

      {/* Zoom controls */}
      <div className="absolute bottom-4 left-1/2 -translate-x-1/2 flex gap-2 z-10">
        <Button
          size="icon"
          variant="ghost"
          className="text-white hover:bg-white/20"
          onClick={(e) => {
            e.stopPropagation();
            onZoomOut();
          }}
          disabled={zoom <= 0.5}
          data-testid="button-zoom-out"
        >
          <ZoomOut className="w-5 h-5" />
        </Button>
        <div className="px-4 py-2 bg-white/10 rounded-md text-white font-noto">
          {Math.round(zoom * 100)}%
        </div>
        <Button
          size="icon"
          variant="ghost"
          className="text-white hover:bg-white/20"
          onClick={(e) => {
            e.stopPropagation();
            onZoomIn();
          }}
          disabled={zoom >= 3}
          data-testid="button-zoom-in"
        >
          <ZoomIn className="w-5 h-5" />
        </Button>
      </div>

      {/* Navigation buttons */}
      {total && total > 1 && (
        <>
          <Button
            size="icon"
            variant="ghost"
            className="absolute left-4 top-1/2 -translate-y-1/2 text-white hover:bg-white/20"
            onClick={(e) => {
              e.stopPropagation();
              onPrevious?.();
            }}
            data-testid="button-previous-image"
          >
            <ChevronLeft className="w-8 h-8" />
          </Button>
          <Button
            size="icon"
            variant="ghost"
            className="absolute right-4 top-1/2 -translate-y-1/2 text-white hover:bg-white/20"
            onClick={(e) => {
              e.stopPropagation();
              onNext?.();
            }}
            data-testid="button-next-image"
          >
            <ChevronRight className="w-8 h-8" />
          </Button>
        </>
      )}

      {/* Image */}
      <div
        className="max-w-[90vw] max-h-[90vh] overflow-auto"
        onClick={(e) => e.stopPropagation()}
      >
        <img
          src={image}
          alt="Lightbox view"
          className="transition-transform duration-200"
          style={{
            transform: `scale(${zoom})`,
            transformOrigin: "center center",
          }}
          data-testid="img-lightbox"
        />
      </div>
    </div>
  );
}

